function BeautyTags(){}BeautyTags.prototype.run=function(){var t=this;t.listenAddTagsEvent(),t.listenEditTagsEvent(),t.listenDelTagsEvent()},BeautyTags.prototype.listenAddTagsEvent=function(){$("#add-btn").click(function(){xfzalert.alertOneInput({title:"请输入要添加的美女标签",placeholder:"美女标签",confirmCallback:function(t){xfzajax.post({url:"/cms/write_beauty_tag/",data:{tag:t},success:function(t){200===t.code?window.location.reload():xfzalert.close()}})}})})},BeautyTags.prototype.listenEditTagsEvent=function(){$(".edit_tag_btn").click(function(){var t=$(this).parent().parent(),a=t.attr("data-pk"),e=t.attr("data-tag");xfzalert.alertOneInput({title:"请输入需要修改的值",value:e,confirmCallback:function(t){t===e?xfzalert.alertErrorToast("跟原来的值一样,不修改乱点什么!"):xfzajax.post({url:"/cms/edit_beauty_tag/",data:{pk:a,tag:t},success:function(t){200===t.code&&window.location.reload()}})}})})},BeautyTags.prototype.listenDelTagsEvent=function(){$(".delete_tag_btn").click(function(){var t=$(this).parent().parent().attr("data-pk");xfzajax.post({url:"/cms/delete_beauty_tag/",data:{pk:t},success:function(t){200===t.code&&(xfzalert.alertSuccessToast("删除标签成功"),window.location.reload())}})})},$(function(){(new BeautyTags).run()});